def addWithoutSyntaxSugar(x: Int) = {
  new Function1[Int, Int]() {
    def apply(y: Int): Int = x + y
  }
}
addWithoutSyntaxSugar(1).
  isInstanceOf[Function1[Int,Int]] should be(__)

