def makeUpper(xs: List[String]) = xs map {_.toUpperCase}

def makeWhatEverYouLike(xs: List[String], sideEffect: String => String) = {
  xs map sideEffect
}

makeUpper(List("abc", "xyz", "123")) should be(__)

makeWhatEverYouLike(List("ABC", "XYZ", "123"), {
  x => x.toLowerCase
}) should be(__)

